import React, { useState } from 'react';

const Skills = () => {
  const [activeCategory, setActiveCategory] = useState('frontend');

  const skillCategories = [
    {
      id: 'frontend',
      name: 'Frontend Development',
      icon: (
        <svg className="w-6 h-6" fill="none" stroke="currentColor" viewBox="0 0 24 24">
          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9.75 17L9 20l-1 1h8l-1-1-.75-3M3 13h18M5 17h14a2 2 0 002-2V5a2 2 0 00-2-2H5a2 2 0 00-2 2v10a2 2 0 002 2z" />
        </svg>
      )
    },
    {
      id: 'backend',
      name: 'Backend Development',
      icon: (
        <svg className="w-6 h-6" fill="none" stroke="currentColor" viewBox="0 0 24 24">
          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M5 12h14M5 12a2 2 0 01-2-2V6a2 2 0 012-2h14a2 2 0 012 2v4a2 2 0 01-2 2M5 12a2 2 0 00-2 2v4a2 2 0 002 2h14a2 2 0 002-2v-4a2 2 0 00-2-2m-2-4h.01M17 16h.01" />
        </svg>
      )
    },
    {
      id: 'tools',
      name: 'Tools & Technologies',
      icon: (
        <svg className="w-6 h-6" fill="none" stroke="currentColor" viewBox="0 0 24 24">
          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M10.325 4.317c.426-1.756 2.924-1.756 3.35 0a1.724 1.724 0 002.573 1.066c1.543-.94 3.31.826 2.37 2.37a1.724 1.724 0 001.065 2.572c1.756.426 1.756 2.924 0 3.35a1.724 1.724 0 00-1.066 2.573c.94 1.543-.826 3.31-2.37 2.37a1.724 1.724 0 00-2.572 1.065c-.426 1.756-2.924 1.756-3.35 0a1.724 1.724 0 00-2.573-1.066c-1.543.94-3.31-.826-2.37-2.37a1.724 1.724 0 00-1.065-2.572c-1.756-.426-1.756-2.924 0-3.35a1.724 1.724 0 001.066-2.573c-.94-1.543.826-3.31 2.37-2.37.996.608 2.296.07 2.572-1.065z" />
          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M15 12a3 3 0 11-6 0 3 3 0 016 0z" />
        </svg>
      )
    },
    {
      id: 'soft',
      name: 'Soft Skills',
      icon: (
        <svg className="w-6 h-6" fill="none" stroke="currentColor" viewBox="0 0 24 24">
          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M17 20h5v-2a3 3 0 00-5.356-1.857M17 20H7m10 0v-2c0-.656-.126-1.283-.356-1.857M7 20H2v-2a3 3 0 015.356-1.857M7 20v-2c0-.656.126-1.283.356-1.857m0 0a5.002 5.002 0 019.288 0M15 7a3 3 0 11-6 0 3 3 0 016 0zm6 3a2 2 0 11-4 0 2 2 0 014 0zM7 10a2 2 0 11-4 0 2 2 0 014 0z" />
        </svg>
      )
    }
  ];

  const skills = {
    frontend: [
      { name: 'React', level: 95, color: 'from-blue-500 to-blue-600' },
      { name: 'JavaScript', level: 90, color: 'from-yellow-500 to-yellow-600' },
      { name: 'TypeScript', level: 85, color: 'from-blue-600 to-blue-700' },
      { name: 'HTML/CSS', level: 95, color: 'from-orange-500 to-orange-600' },
      { name: 'Tailwind CSS', level: 90, color: 'from-cyan-500 to-cyan-600' },
      { name: 'Next.js', level: 80, color: 'from-gray-800 to-gray-900' }
    ],
    backend: [
      { name: 'Node.js', level: 88, color: 'from-green-500 to-green-600' },
      { name: 'Python', level: 85, color: 'from-blue-500 to-blue-600' },
      { name: 'Express.js', level: 90, color: 'from-gray-600 to-gray-700' },
      { name: 'MongoDB', level: 82, color: 'from-green-600 to-green-700' },
      { name: 'PostgreSQL', level: 78, color: 'from-blue-600 to-blue-700' },
      { name: 'GraphQL', level: 75, color: 'from-pink-500 to-pink-600' }
    ],
    tools: [
      { name: 'Git', level: 92, color: 'from-orange-500 to-orange-600' },
      { name: 'Docker', level: 80, color: 'from-blue-500 to-blue-600' },
      { name: 'AWS', level: 75, color: 'from-yellow-500 to-yellow-600' },
      { name: 'Figma', level: 85, color: 'from-purple-500 to-purple-600' },
      { name: 'VS Code', level: 95, color: 'from-blue-500 to-blue-600' },
      { name: 'Postman', level: 88, color: 'from-orange-500 to-orange-600' }
    ],
    soft: [
      { name: 'Problem Solving', level: 90, color: 'from-green-500 to-green-600' },
      { name: 'Communication', level: 88, color: 'from-blue-500 to-blue-600' },
      { name: 'Team Leadership', level: 85, color: 'from-purple-500 to-purple-600' },
      { name: 'Time Management', level: 92, color: 'from-yellow-500 to-yellow-600' },
      { name: 'Adaptability', level: 87, color: 'from-indigo-500 to-indigo-600' },
      { name: 'Creativity', level: 83, color: 'from-pink-500 to-pink-600' }
    ]
  };

  return (
    <div className="pt-16">
      <section className="section-padding">
        <div className="container-custom">
          {/* Header */}
          <div className="text-center mb-16">
            <h1 className="text-4xl md:text-5xl font-bold text-secondary-900 mb-4">
              My <span className="gradient-text">Skills</span>
            </h1>
            <p className="text-lg text-secondary-600 max-w-2xl mx-auto">
              I've developed a diverse set of skills through years of experience and continuous learning. 
              Here's what I bring to the table.
            </p>
          </div>

          {/* Category Tabs */}
          <div className="flex flex-wrap justify-center gap-4 mb-12">
            {skillCategories.map((category) => (
              <button
                key={category.id}
                onClick={() => setActiveCategory(category.id)}
                className={`flex items-center gap-2 px-6 py-3 rounded-lg font-medium transition-all duration-200 ${
                  activeCategory === category.id
                    ? 'bg-primary-600 text-white shadow-lg'
                    : 'bg-secondary-100 text-secondary-700 hover:bg-secondary-200'
                }`}
              >
                {category.icon}
                {category.name}
              </button>
            ))}
          </div>

          {/* Skills Grid */}
          <div className="grid md:grid-cols-2 gap-8">
            {skills[activeCategory].map((skill, index) => (
              <div key={index} className="bg-white rounded-xl p-6 shadow-lg card-hover">
                <div className="flex justify-between items-center mb-3">
                  <h3 className="text-lg font-semibold text-secondary-900">
                    {skill.name}
                  </h3>
                  <span className="text-sm font-medium text-secondary-600">
                    {skill.level}%
                  </span>
                </div>
                <div className="w-full bg-secondary-200 rounded-full h-3">
                  <div
                    className={`h-3 rounded-full bg-gradient-to-r ${skill.color} transition-all duration-1000 ease-out`}
                    style={{ width: `${skill.level}%` }}
                  ></div>
                </div>
              </div>
            ))}
          </div>

          {/* Additional Skills */}
          <div className="mt-16">
            <h2 className="text-2xl font-bold text-secondary-900 text-center mb-8">
              Other Technologies I Work With
            </h2>
            <div className="grid grid-cols-2 md:grid-cols-4 lg:grid-cols-6 gap-4">
              {[
                'Redux', 'Firebase', 'Jest', 'Webpack', 'Babel', 'Sass',
                'Bootstrap', 'Material-UI', 'Ant Design', 'Three.js', 'D3.js', 'Socket.io'
              ].map((tech, index) => (
                <div
                  key={index}
                  className="bg-white rounded-lg p-4 text-center shadow-md hover:shadow-lg transition-shadow duration-200"
                >
                  <span className="text-sm font-medium text-secondary-700">{tech}</span>
                </div>
              ))}
            </div>
          </div>

          {/* Learning Section */}
          <div className="mt-16 bg-gradient-to-r from-primary-50 to-purple-50 rounded-2xl p-8">
            <div className="text-center">
              <h3 className="text-2xl font-bold text-secondary-900 mb-4">
                Currently Learning
              </h3>
              <p className="text-secondary-600 mb-6">
                I'm always expanding my skill set. Here's what I'm currently focused on:
              </p>
              <div className="flex flex-wrap justify-center gap-4">
                {['Rust', 'WebAssembly', 'Machine Learning', 'Blockchain'].map((skill, index) => (
                  <span
                    key={index}
                    className="px-4 py-2 bg-white rounded-full text-sm font-medium text-secondary-700 shadow-sm"
                  >
                    {skill}
                  </span>
                ))}
              </div>
            </div>
          </div>
        </div>
      </section>
    </div>
  );
};

export default Skills;
